def comp(nilai):
  if nilai%2 == 1 :
    print = "Aneh"
  elif (nilai%2 == 0) and (2<= nilai <=5) :
    print = "Tidak Aneh"
  elif (nilai%2 == 0) and (6<= nilai <=20) :
    print = "Aneh"
  elif (nilai%2 == 0) and (nilai > 20) :
    print = "Tidak Aneh"
  return print

